mapper.update:
  /* com.m2m.fo.bd.mapper.BdMainMapper.likeUpdate */
		MERGE INTO BD_INTRST_DTL AS C
		USING (SELECT 1 AS DUAL) AS D 
		ON (C.BID_ENTRPS_NO = #{bidEntrpsNo} AND C.BID_PBLANC_ID = #{bidPblancId})
		WHEN MATCHED THEN 
		    UPDATE SET     
				 C.DELETE_AT = #{likeYn}     
				 ,C.LAST_CHANGER_ID = #{bidEntrpsNo}
		         ,C.LAST_CHANGE_DT = GETDATE()
		  WHEN NOT MATCHED THEN
		    INSERT 
		(		BID_ENTRPS_NO
				,BID_PBLANC_ID
				,DELETE_AT
				,DELETE_DT
				,FRST_REGISTER_ID
				,FRST_REGIST_DT
				,LAST_CHANGER_ID
				,LAST_CHANGE_DT
				)     
		    VALUES
			   (#{bidEntrpsNo}
			   ,#{bidPblancId}
			   ,#{likeYn}
			   ,GETDATE()
			   ,#{bidEntrpsNo}
			   ,GETDATE()
			   ,#{bidEntrpsNo}
			   ,GETDATE()
			   );

  /* com.m2m.fo.bd.mapper.BdMainMapper.bidLikeUpdate */
	UPDATE BD_BID_BAS
	SET INTRST_ENTRPS_QY = (SELECT INTRST_ENTRPS_QY FROM BD_BID_BAS WHERE BID_PBLANC_ID = #{bidPblancId}) + #{likeCnt}
	WHERE BID_PBLANC_ID = #{bidPblancId}

mapper.select:
 /* com.m2m.fo.bd.mapper.BdMainMapper.bdMypageLikeCnt 마이페이지 목록 조회 */
SELECT  ISNULL(COUNT(*),0)  AS FAVORITES_CNT
FROM BD_INTRST_DTL
WHERE BID_ENTRPS_NO = #{bidEntrpsNo}
	AND DELETE_AT='N'
